import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CenterDashboard.css';

export default function CenterDashboard() {
  const token = localStorage.getItem('token');
  const [missions, setMissions] = useState([]);
  const today = new Date().toISOString().split('T')[0];

  const isToday = (dateStr) => {
    const taskDate = new Date(dateStr).toISOString().split('T')[0];
    return taskDate === today;
  };

  const fetchMissions = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API}/api/tasks`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const filtered = res.data
        .filter((t) => isToday(t.createdAt))
        .map((t) => {
          const [timeRange, desc] = t.description.split(': ');
          const [start, end] = timeRange.split(' - ');
          return {
            title: t.title,
            start: start?.trim() || 'N/A',
            end: end?.trim() || 'N/A',
            description: desc?.trim() || t.description
          };
        });
      setMissions(filtered);
    } catch (err) {
      console.error('Failed to fetch missions:', err);
    }
  };

  useEffect(() => {
    fetchMissions();
  }, []);

  return (
    <div className="center-dashboard">
      <div className="mood-header">
        <img
          src="https://i.imgur.com/LzI3M0K.png"
          alt="Avatar"
          className="avatar"
        />
        <h1 className="quote">‚ÄúToday, I bloom in pink power üå∏‚Äù</h1>
      </div>

      <div className="mission-list">
        <h2>üéØ Missions for Today</h2>
        <ul>
          {missions.length === 0 ? (
            <p>No missions added yet. Click ‚ÄúAdd a Mission‚Äù from sidebar!</p>
          ) : (
            missions.map((m, idx) => (
              <li key={idx} className="mission-card">
                <h3>{m.title}</h3>
                <p><strong>Start:</strong> {m.start} | <strong>End:</strong> {m.end}</p>
                <p>{m.description}</p>
              </li>
            ))
          )}
        </ul>
      </div>
    </div>
  );
}
