import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Sortable from 'react-sortablejs';
import './AddTaskPage.css';

export default function AddTaskPage() {
  const navigate = useNavigate();
  const today = new Date().toISOString().split('T')[0];

  const [tasks, setTasks] = useState([
    { id: '1', title: 'Workout', start: '6:00 AM', end: '6:45 AM', description: 'Yoga & warm-up' },
    { id: '2', title: 'Study', start: '7:00 AM', end: '9:00 AM', description: 'DSA Revision' }
  ]);

  const [newTask, setNewTask] = useState({
    title: '',
    start: '',
    end: '',
    description: ''
  });

  const handleAddTask = () => {
    const newId = (tasks.length + 1).toString();
    setTasks([...tasks, { id: newId, ...newTask }]);
    setNewTask({ title: '', start: '', end: '', description: '' });
  };

  const finalizeTasks = () => {
    // For now just console log (you can later POST this to backend)
    console.log('Final Tasks:', tasks);
    navigate('/');
  };

  return (
    <div className="add-task-page">
      <h2>➕ Today's Missions ({today})</h2>

      <Sortable
        tag="ul"
        onChange={(order) => {
          const orderedTasks = order.map(id => tasks.find(t => t.id === id));
          setTasks(orderedTasks);
        }}
      >
        {tasks.map((task) => (
          <li key={task.id} data-id={task.id} className="task-card">
            <h3>{task.title}</h3>
            <p><strong>{task.start} – {task.end}</strong></p>
            <p>{task.description}</p>
          </li>
        ))}
      </Sortable>

      <div className="new-task-form">
        <h3>Add New Mission</h3>
        <input
          type="text"
          placeholder="Title"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Start Time"
          value={newTask.start}
          onChange={(e) => setNewTask({ ...newTask, start: e.target.value })}
        />
        <input
          type="text"
          placeholder="End Time"
          value={newTask.end}
          onChange={(e) => setNewTask({ ...newTask, end: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newTask.description}
          onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
        ></textarea>
        <button onClick={handleAddTask}>➕ Add Mission</button>
      </div>

      <button className="finalize-btn" onClick={finalizeTasks}>✅ Finalize & Return</button>
    </div>
  );
}
