import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ReactSortable } from 'react-sortablejs';
import axios from 'axios';
import './AddTaskPage.css';

function AddTaskPage() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const today = new Date().toISOString().split('T')[0];

  const [tasks, setTasks] = useState([]);
  const [newTaskFormVisible, setNewTaskFormVisible] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    start: '',
    end: '',
    description: ''
  });

  // Filter only today's tasks
  const isToday = (dateStr) => {
    const createdDate = new Date(dateStr).toISOString().split('T')[0];
    return createdDate === today;
  };

  const fetchTasks = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API}/api/tasks`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const filtered = res.data.filter((t) => isToday(t.createdAt));
      setTasks(filtered.map((t, i) => ({ ...t, id: t._id || `${i}` })));
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  const handleAddTask = async () => {
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API}/api/tasks`,
        {
          title: newTask.title,
          description: `${newTask.start} - ${newTask.end}: ${newTask.description}`
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNewTask({ title: '', start: '', end: '', description: '' });
      setNewTaskFormVisible(false);
      fetchTasks(); // refresh
    } catch (err) {
      console.error('Error adding task:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API}/api/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchTasks();
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const finalizeTasks = () => {
    navigate('/');
  };

  return (
    <div className="add-task-page">
      <h2>‚ûï Today's Missions ({today})</h2>

      <ReactSortable list={tasks} setList={setTasks}>
        {tasks.map((task) => (
          <li key={task.id} className="task-card">
            <div>
              <h3>{task.title}</h3>
              <p>{task.description}</p>
            </div>
            <button className="delete-btn" onClick={() => handleDelete(task.id)}>üóëÔ∏è</button>
          </li>
        ))}
      </ReactSortable>

      {!newTaskFormVisible ? (
        <button className="show-form-btn" onClick={() => setNewTaskFormVisible(true)}>
          ‚ûï Add a Mission
        </button>
      ) : (
        <div className="new-task-form">
          <input
            type="text"
            placeholder="Title"
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Start Time"
            value={newTask.start}
            onChange={(e) => setNewTask({ ...newTask, start: e.target.value })}
          />
          <input
            type="text"
            placeholder="End Time"
            value={newTask.end}
            onChange={(e) => setNewTask({ ...newTask, end: e.target.value })}
          />
          <textarea
            placeholder="Description"
            value={newTask.description}
            onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
          ></textarea>
          <button onClick={handleAddTask}>‚ûï Save Task</button>
        </div>
      )}

      <button className="finalize-btn" onClick={finalizeTasks}>
        ‚úÖ Finalize & Return
      </button>
    </div>
  );
}

export default AddTaskPage;
