import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

export default function Dashboard() {
  const [tasks, setTasks] = useState([]);
  const [msg, setMsg] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const token = localStorage.getItem('token');

  const fetchTasks = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API}/api/tasks`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      setMsg('Failed to fetch tasks');
    }
  };

  const createTask = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${process.env.REACT_APP_API}/api/tasks`, { title, description }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTitle('');
      setDescription('');
      fetchTasks();
    } catch (err) {
      setMsg('Failed to create task');
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API}/api/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTasks();
    } catch (err) {
      setMsg('Failed to delete task');
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="dashboard-container">
      <h2>ðŸ’— My Pretty Task Manager ðŸ’—</h2>
      {msg && <p className="error">{msg}</p>}

      <form onSubmit={createTask} className="task-form">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Task Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button type="submit">âž• Add Task</button>
      </form>

      <ul className="task-list">
        {tasks.map((task) => (
          <li key={task._id} className="task-card">
            <div>
              <strong>{task.title}</strong>
              <p>{task.description}</p>
            </div>
            <button onClick={() => deleteTask(task._id)}>âŒ</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
